def findAdjecent(postions):
    count = 0
    for idx,row in enumerate(postions):
        for i in range(len(row)):
            sub_count = 0
            if postions[idx][i] == ".":
                continue
            for dr in range(-1,2):
                for dc in range(-1,2):
                    if dr == 0 and dc == 0:
                        continue
                    neg_row = idx + dr
                    neg_col = i + dc
                    if (neg_row >= 0 and neg_row <= len(row) -1) and (neg_col >= 0 and neg_col <= len(row) -1):
                        if postions[dr+idx][dc+i] == "@":
                            sub_count += 1
            if sub_count < 4 and row[i] == "@":
                count+= 1
    return count
